mapper.insert:

    SELECT currval(pg_get_serial_sequence('users', 'user_id'))

INSERT INTO users (
user_name,
password
) VALUES (
#{userName},
#{password}
)

mapper.update:
UPDATE users
SET
user_name = #{userName},
password = #{password}
WHERE user_id = #{userId}

UPDATE users
SET
role = #{role}
WHERE user_id = #{userId}
